import React, {Component} from "react"
import CarTableRow from "./CarTableRow"
import ProductsPage from "./ProductsPage";


export default class CarTable extends Component 
{
    handleNameClick = e =>
    {
        let CarTable = this.props.cars.sort((a, b) => a.model > b.model ? 1 : -1)
        this.setState({CarTable})
    }

    handlePriceClick = e =>
    {
        let CarTable = this.props.cars.sort((a, b) => a.year > b.year ? 1 : -1)
        this.setState({CarTable: CarTable})
    }


    handleYear2020Click = e =>
    {
        let CarTable = this.props.cars.sort((a) => a.model = ("Blah") ? 1 : -1)
        this.setState({CarTable: CarTable})
    }

    handleYear2021Click = e =>
    {
        this.setState({selectedProducts: this.props.cars.filter(a => a.price >= 2021)})
    }

    handleYear2022Click = e =>
    {
        let CarTable = this.props.cars.filter(a => a.year >= 2022)
        this.setState({CarTable})
    }

    handleRegionsChange = e =>
    {
        this.setState({CarTable: e.target.value})
        if(e.target.value === "All Regions") // all countries
        {
            this.setState({CarTable: this.props.cars})
        }
        else  // countries from one region
        {
            this.setState({CarTable: this.props.cars.filter(car => car.colour === e.target.value)})
        }
    }
    render() 
    {
        return (
            <div>
            <h5>Sort By</h5>
        <ul className="sorts">
            <p>Price</p>
            <input className="sortItems" type={"checkbox"} onClick={this.handlePriceClick}/>
            <p>Name</p>
            <input className="sortItems" type={"checkbox"} onClick={this.handleNameClick}/>

        </ul>


                <select
                    name="cars"
                    onChange={this.handleRegionsChange}
                >
                        <option onClick={this.handleNameClick} >Hoodies</option>
                    <option>Shoes</option>

                    <option>Skateboards</option>




                </select>



                <div  className="filterBar">
            <h6>Year</h6>
            <ul className="sorts">
                <p>Above 2022</p>
                <input className="sortItems" type={"checkbox"} onClick={this.handleYear2022Click}/>
                <p>Above 2021</p>
                <input className="sortItems" type={"checkbox"}onClick={this.handleYear2021Click}/>
                <p>Above 2020</p>
                <input className="sortItems" type={"checkbox"}onClick={this.handleYear2020Click}/>
            </ul>

            <div className="Products">
            {this.props.cars.map((car) => <CarTableRow key={car._id} car={car}/>)}
                </div>
        </div>
            </div>

        )
    }
}
