import React, {Component} from "react"
import CarTableRow from "./CarTableRow"
import ProductsPage from "./ProductsPage";


export default class CarTable extends Component 
{
    handleNameClick = e =>
    {
        let CarTable = this.props.cars.sort((a, b) => a.model > b.model ? 1 : -1)
        this.setState({CarTable})
    }

    handlePriceClick = e =>
    {
        let CarTable = this.props.cars.sort((a, b) => a.price > b.price ? 1 : -1)
        this.setState({ProductTable: CarTable})
    }


    handleYear2020Click = e =>
    {
        let ProductTable = this.props.cars.filter(a => a.year === 2020)
        this.setState({ProductTable})
    }

    handleYear2021Click = e =>
    {
        this.setState({selectedProducts: this.props.cars.filter(a => a.year === 2021)})
    }

    handleYear2022Click = e =>
    {
        let selectedProducts = this.props.cars.filter(a => a.year === 2022)
        this.setState({selectedProducts})
    }


    render() 
    {
        return (

            <div className="Products">
            {this.props.cars.map((car) => <CarTableRow key={car._id} car={car}/>)}
                </div>
        )
    }
}
