import React, {Component} from "react"
import {Redirect, Link} from "react-router-dom"
import axios from "axios"

import LinkInClass from "../components/LinkInClass"
import {ACCESS_LEVEL_ADMIN, ACCESS_LEVEL_GUEST, SERVER_HOST} from "../config/global_constants"
import * as jwt from "node/punycode";


export default class ShoppingCart extends Component
{
    constructor(props)
    {
        super(props)

        this.state = {
            totalQTY:null,
            totalPrice:null,
            isShoppingIn:false,
            wasSubmittedAtLeastOnce1:false
        }
    }
    componentDidMount() {
        const token2 = jwt.decode(localStorage.token,{algorithimn:'HS256'})
        if (localStorage.accessLevel>ACCESS_LEVEL_GUEST){
            axios.get(`${SERVER_HOST}/users/${token2.id}`, {headers:{"authorisation": localStorage.token}})
                .then(res => {
                    console.log(res.data)
                    if (res.data){
                        if (res.data.errorMessage){
                            console.log(res.data.errorMessage)
                        }
                        else{
                            console.log(res.data)
                            if (res.data.cart != null){
                                this.setState({
                                    cart:res.data.cart
                                })
                            }
                        }
                    }else{
                        console.log("User Data Not Returned")
                    }
                })
        }
    }

    render()
    {
        let errorMessage = "";
        if(this.state.wasSubmittedAtLeastOnce1)

            errorMessage = <div className="error">Login Details are incorrect<br/></div>;
        return (
            <div>
            <div>shopping</div>

                {
                localStorage
                ? <div  src={`${localStorage.ProductName}`} alt=""/>
                : null

                }
        {
            localStorage
                ? <div className="add-new-car">
                    <Link className="blue-button" to={"/AddCar"}>Add New Product</Link>
                    <Link className="blue-button" to={"/ResetDatabase"}>Reset Database</Link>
                </div>
                : null
        }

            </div>
        )
    }}
