import React, {Component} from "react"
import CheckoutBasketTableRow from "./CheckoutBasketTableRow";
import BuyCar from "./BuyCar";
import {ACCESS_LEVEL_GUEST, ACCESS_LEVEL_NORMAL_USER} from "../config/global_constants";

export default class CheckoutBasketTable extends Component
{
    constructor(props)
    {
        super(props)

       this.state={
            totalPrice:0
       }
    }

    handleNameClick = e =>
    {
        this.state.totalPrice=0;

        this.state.totalPrice += this.props.cars.price

    }





    render()
    {


        const tableBody=this.state.cart.map((car =>
                <tr key={car.model}><td >{car.colour}</td><td >{car.price}</td><td >{car.year}</td>
                </tr>
        ));
        const total=(this.state.data.reduce((total,currentItem) =>  total = total + currentItem.price , 0 ));

        let soldOrForSale = null
        if(localStorage.accessLevel <= ACCESS_LEVEL_GUEST    )
        {
            if(this.props.cars.sold !== true)
            {
                soldOrForSale = <BuyCar carID={this.props.cars._id} price={this.state.totalPrice} />
            }
            else
            {
                soldOrForSale = "SOLD"
            }
        }
        if(localStorage.accessLevel <= ACCESS_LEVEL_NORMAL_USER    )
        {
            if(this.props.cars.sold !== true)
            {
                soldOrForSale = <BuyCar carID={this.props.cars._id} price={this.state.totalPrice} />
            }
            else
            {
                soldOrForSale = "SOLD"
            }
        }
        return (
            <table>
                <thead>
                <tr>
                    <th>Model</th>
                    <th>Colour</th>
                    <th>Year</th>
                    <th>Price</th>
                    <th>Photos</th>
                    <th> </th>
                </tr>
                </thead>

                <tbody>
                {this.props.cars.map((car) => <CheckoutBasketTableRow key={car._id} car={car}/>)}
                </tbody>
                <div onClick={this.handleNameClick}>{total
                   }{soldOrForSale}</div>
            </table>
        )
    }
}
