import React, {Component} from "react"
import CarTableRow from "./CarTableRow"
import ProductsPage from "./ProductsPage";


export default class CarTable extends Component 
{
    handleNameClick = e =>
    {
        let CarTable = this.props.cars.sort((a, b) => a.model > b.model ? 1 : -1)
        this.setState({CarTable})
    }

    handlePriceClick = e =>
    {
        let CarTable = this.props.cars.sort((a, b) => a.price > b.price ? 1 : -1)
        this.setState({ProductTable: CarTable})
    }


    handleYear2020Click = e =>
    {
        let CarTable = this.props.cars.filter(a => a.year === 2020)
        this.setState({CarTable})
    }

    handleYear2021Click = e =>
    {
        this.setState({selectedProducts: this.props.cars.filter(a => a.year === 2021)})
    }

    handleYear2022Click = e =>
    {
        let CarTable = this.props.cars.filter(a => a.year === 2022)
        this.setState({CarTable})
    }


    render() 
    {
        return (
            <div>
            <h5>Sort By</h5>
        <ul className="sorts">
            <li className="sortItems" onClick={this.handlePriceClick}>Price</li>
            <li className="sortItems" onClick={this.handleNameClick}>Name</li>

        </ul>






        <div  className="filterBar">
            <h6>Year</h6>
            <ul className="sorts">
                <li className="sortItems" onClick={this.handleYear2022Click}>2022</li>
                <li className="sortItems" onClick={this.handleYear2021Click}>2021</li>
                <li className="sortItems" onClick={this.handleYear2020Click}>2020</li>
            </ul>

            <div className="Products">
            {this.props.cars.map((car) => <CarTableRow key={car._id} car={car}/>)}
                </div>
        </div>
            </div>

        )
    }
}
