import React, {Component} from "react"
import {Redirect, Link} from "react-router-dom"
import axios from "axios"

import LinkInClass from "../components/LinkInClass"
import {SERVER_HOST} from "../config/global_constants"


export default class Login extends Component
{
    constructor(props)
    {
        super(props)

        this.state = {
            email:"",
            password:"",
            isLoggedIn:false,
            wasSubmittedAtLeastOnce:false
        }
    }


    handleChange = (e) => 
    {
        this.setState({[e.target.name]: e.target.value})
    }


    handleSubmit = (e) => 
    {
        axios.post(`${SERVER_HOST}/users/login/${this.state.email}/${this.state.password}`)
        .then(res => 
        {     
            localStorage.name = res.data.name
            localStorage.accessLevel = res.data.accessLevel
            localStorage.profilePhoto = res.data.profilePhoto                        
            localStorage.token = res.data.token

            this.setState({isLoggedIn:true})
        }) 
        .catch(err =>
        {
            this.setState({wasSubmittedAtLeastOnce: true})
        })
    }


    render()
    {         
        let errorMessage = "";
        if(this.state.wasSubmittedAtLeastOnce)
        {
            errorMessage = <div className="error">Login Details are incorrect<br/></div>;
        }

        return (
            <div>

                <h2>Login</h2>

                {this.state.isLoggedIn ? <Redirect to="/DisplayAllCars"/> : null} 

        <div className="container login-container" >
            <div className="row">
                <div className="col-md-6 login-form-1">
                    <h3>Login for Form 1</h3>
                    <form  className="form-container" noValidate = {true} id = "loginOrRegistrationForm">
                        <div className="form-group">
                            <input
                                type = "email"
                                name = "email"
                                placeholder = "Email"
                                autoComplete="email"
                                value={this.state.email}
                                onChange={this.handleChange}
                            />
                        </div>
                        <div className="form-group">
                            <input
                                type = "password"
                                name = "password"
                                placeholder = "Password"
                                autoComplete="password"
                                value={this.state.password}
                                onChange={this.handleChange}
                            />
                        </div>
                        <div className="form-group">
                            <LinkInClass type="submit" className="btnSubmit" value="Login" onClick={this.handleSubmit}/>
                        </div>
                        <div className="form-group">
                            <Link className="red-button" to={"/DisplayAllCars"}>Cancel</Link>
                        </div>
                    </form>
                </div>
            </div>
        </div>
            </div>
        )
    }
}
